import React, {useState} from "react";
import { setUserSession } from "./service/AuthService";
import axios from 'axios';

const loginAPIUrl = 'https://yv8a67sww8.execute-api.us-east-1.amazonaws.com/prod/login';

const Login = (props) => {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState(null);

    const submitHandler = (event) => {
      event.preventDefault();
      if(username.trim() === '' || password.trim() === ''){
        setErrorMessage('Both username and password are required!');
        return;
      }

      setErrorMessage(null);
      const requestConfig = {
        headers: {
            'x-api-key': 'CqIRvghfaQlNhPna9d7P9q39aRmEwMT87auwBmF0'
        }
      }

      const requestBody = {
        username: username,
        password: password
      }
      axios.post(loginAPIUrl, requestBody, requestConfig).then((response) => {
         setUserSession(response.data.user, response.data.token);
         props.history.push('/file-upload');
      }).catch((error) => {
        if(error.response.status === 401 || error.response.status === 403){
          setErrorMessage(error.response.data.message);
        }else{
          setErrorMessage('sorry... the backend is down! please try again later!');
        }
      })
    }

    return (
        <div className="container">
            <h2> Filesurfer Converter System </h2>
            <br/>
            <h3> File Upload with React and a Serverless API! </h3>
            <br/><br/>
            <div>
                <form>
                    username <input type="text" value = {username} onChange={event => setUsername(event.target.value)}/><br/>
                    <input class="button" style={{'--clr': '#1ab5d4'}} type="submit" value="Login" />
                </form>
            </div>
            <br/><br/>
            <input class="button" style={{'--clr': '#1ab5d4'}} type="button" value="Logout" onClick={logoutHandler}/>
        </div>    
    )
}

export default Login;
