import React from "react";
import {getUser, resetUserSession} from './service/AuthService';
import { render } from "@testing-library/react";
import axios from "axios";

class FileUpload extends Component{
//const FileUpload = (props) => {

    user = getUser();
    name = user !== 'undefined' && user ? user.name : '';

    logoutHandler = () => {
        resetUserSession();
        props.history.push('/login');
    }

    ///////////////////////////////////////////////////////////

    state = {
        selectedFile: null,
        fileUploadSuccessfully: false
    }

    onFileChange = event => {
        this.setState({selectedFile: event.target.files[0]});
    }

    onFileUpload = () => {
        const formData = new FormData();
        formData.append(
            "demo file",
            this.state.selectedFile,
            this.state.selectedFile.name
        )
        //call api to upload the file
        axios.post(" https://yv8a67sww8.execute-api.us-east-1.amazonaws.com/prod/file-upload", formData).then(() => {
            this.setState({selectedFile: null});
            this.setState({fileUploadSuccessfully: true});
        })

    }

    fileData = () => {
        if(this.state.selectedFile){
          var date = new Date(this.state.selectedFile.lastModified);
          return(
            <div>
              <h2>File Details:</h2>
              <p>File Name: {this.state.selectedFile.name}</p>
              <p>File Type: {this.state.selectedFile.type}</p>
              <p>Last Modified: {" "}
                  {date.getDate()+ "-" + date.getMonth() + "-" + date.getFullYear() + " at time: " + date.getHours() + ":" + date.getMinutes()}
              </p>
            </div>
          );
        } else if(this.state.fileUploadSuccessfully){
          return (
            <div>
              <br />
              <h4>Your file has been successfully uploaded! </h4>
            </div>
          );
        } else {
          return (
            <div>
              <br />
              <h4>Choose a file and then press the Upload button! </h4>
            </div>
          )
        }
    }

    ///////////////////////////////////////////////////////////

    render() {
        return (
            <div className="container">
                <h2> Filesurfer Converter System </h2>
                <h3> File Upload with React and a Serverless API! </h3>
                <div>
                    <input type="file" onChange={this.onFileChange} />
                    <button onClick={this.onFileUpload}>
                        Upload
                    </button>
                </div>
                {this.fileData()}
                Hello {name}! You have been loggined in!!! Welcome to the jungle! <br/>
                <input type="button" value="Logout" onClick={logoutHandler}/>
            </div>    
        )
    }
}

export default FileUpload;
