import React, {useState} from "react";
import { resetUserSession } from "./service/AuthService";
import axios from 'axios';

const downloadAPIUrl = 'https://yv8a67sww8.execute-api.us-east-1.amazonaws.com/prod/download';

const Download = (props) => {

    const [filename, setFilename] = useState('');
    const [errorMessage, setErrorMessage] = useState(null);

    let state = {
        chosenFile: null,
        fileDownloadSuccessfully: false,
    }

    const submitHandler = (event) => {
      event.preventDefault();
      if(filename.trim() === ''){
        setErrorMessage('Filename is required!');
        return;
      }

      setErrorMessage(null);
      const requestConfig = {
        headers: {
            'x-api-key': 'CqIRvghfaQlNhPna9d7P9q39aRmEwMT87auwBmF0'
        }
      }

      const requestBody = {
        filename: filename
      }

      axios.post(downloadAPIUrl, requestBody, requestConfig).then((response) => {
         //props.history.push('/download');

      }).catch((error) => {
        if(error.response.status === 401 || error.response.status === 403){
          setErrorMessage(error.response.data.message);
        }else{
          setErrorMessage('sorry... the backend is down! please try again later!');
        }
      })
    }

    const logoutHandler = () => {
        resetUserSession();
        this.props.history.push('/login');
    }

    const fileData = () => {
        if(this.state.selectedFile){
          var date = new Date(this.state.selectedFile.lastModified);
          return(
            <div>
              <h2>File Details:</h2>
              <p>File Name: {this.state.selectedFile.name}</p>
              <p>File Type: {this.state.selectedFile.type}</p>
              <p>Last Modified: {" "}
                  {date.getDate()+ "-" + date.getMonth() + "-" + date.getFullYear() + " at time: " + date.getHours() + ":" + date.getMinutes()}
              </p>
            </div>
          );
        } else if(this.state.fileUploadSuccessfully){
          return (
            <div>
              <br />
              <h4>Your file has been successfully uploaded! </h4>
            </div>
          );
        } else {
          return (
            <div>
              <br />
              <h4>Choose a file and then press the Upload button! </h4>
            </div>
          )
        }
    }


    return (
        <div className="container">
            <h2> Filesurfer Converter System </h2>
            <br/>
            <h3> File Upload with React and a Serverless API! </h3>
            <br/><br/>
            <div>
                <form onSubmit={submitHandler}>
                    filename <input type="text" value = {filename} onChange={event => setFilename(event.target.value)}/><br/>
                    <input class="button" style={{'--clr': '#1ab5d4'}} type="submit" value="Download" />
                </form>
            </div>
            <br/><br/>
            {fileData()}
            <input class="button" style={{'--clr': '#1ab5d4'}} type="button" value="Logout" onClick={logoutHandler}/>
            {errorMessage && <p className="message">{errorMessage}</p>}
        </div>    
    )
}

export default Download;
