import { BrowserRouter, NavLink, Route, Switch } from "react-router-dom";
import Home from "./Home";
import Register from "./Register";
import Login from "./Login";
import FileUpload from "./FileUpload";
import PublicRoute from "./routes/PublicRoute";
import PrivateRoute from "./routes/PrivateRoutes";
import React, { useEffect, useState } from "react";
import { getUser, getToken, setUserSession, resetUserSession } from "./service/AuthService";
import axios from "axios";

const verifyTokenAPIUrl = 'https://yv8a67sww8.execute-api.us-east-1.amazonaws.com/prod/verify';

function App() {

  const [isAuthenticating, setAuthenticating] = useState(true);

  useEffect(() => {
    const token = getToken();
    if(token === 'undefined' || token === undefined || token === null || !token){
      return;
    }

    const requestConfig = {
      headers: {
          'x-api-key': 'CqIRvghfaQlNhPna9d7P9q39aRmEwMT87auwBmF0'
      }
    }

    const requestBody = {
      user: getUser(),
      token: token
    }

    axios.post(verifyTokenAPIUrl, requestBody, requestConfig).then(response => {
      setUserSession(response.data.user, response.data.token);
      setAuthenticating(false); //se il token è stato validato
    }).catch(() => {
      resetUserSession();
      setAuthenticating(false); //se l'autenticazione è completata
    })

  }, []);

  const token = getToken();
  if(isAuthenticating && token){
    return <div className="content"> Authenticating ...</div>
  }

  return (
    <div className="App">
      <BrowserRouter>
      <div>
        <nav className="links">
          <NavLink exact activeClassName="active" to="/">Home</NavLink>
          <NavLink activeClassName="active" to="/register">Register</NavLink>
          <NavLink activeClassName="active" to="/login">Login</NavLink>
          <NavLink activeClassName="active" to="/file-upload">Upload</NavLink>
        </nav>

      </div>
      <div className="content">
        <Switch>
          <Route exact path="/" component={Home}/>
          <PublicRoute path="/register" component={Register}/>
          <PublicRoute path="/login" component={Login}/>
          <PrivateRoute path="/file-upload" component={FileUpload}/>
        </Switch>
      </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
